export interface EthnicGroup {
    id: string;
    name: string;
    vietnameseName: string;
    otherNames?: string[];
    region: string;
    population: string;
    languageGroup: string;
    images: string[];
    introduction: string;
    details: {
        origin: string;
        history: string;
        distribution: string;
        population: string;
        language: string;
        characteristics: string;
        customs: string;
        belief: string;
        housing: string;
        clothing: string;
        cuisine: string;
        production: string;
        arts: string;
        education?: string;
    };
    sources: { name: string; url: string; }[];
}

export const getEthnicGroupById = (id: string): EthnicGroup | undefined => {
    const search = id.toLowerCase().trim();
    return ethnicGroups.find(g =>
        g.id.toLowerCase() === search ||
        g.name.toLowerCase() === search ||
        g.vietnameseName.toLowerCase() === search
    );
};

